import { createSlice } from "@reduxjs/toolkit";
// import db from "../../db";

export const productsSlice= createSlice({
  name: "products",
  initialState: {
    value: {
      name: "",
      email: "",
      favourites: [],
      status: false,
    },
  },
  reducer: {
    login: (state, action) => {
      state.value = action.payload;
    },
  }
});
export const { login } = productsSlice.actions;

export default productsSlice.reducer;
