import { createSlice } from "@reduxjs/toolkit";
// import db from "../../db";

export const accountSlice= createSlice({
  name: "account",
  initialState: {
    value: {
      name: "",
      email: "",
      favourites: [],
      status: false,
    },
  },
  reducer: {
    login: (state, action) => {
      state.value = action.payload;
    },
  }
});
export const { login } = accountSlice.actions;

export default a.reducer;
